		return nil, nil, err
	}
	aead, err := cipher.NewGCM(block)
	if err != nil {
		return nil, nil, err
	}
	nonce, err = secureRand(aead.NonceSize())
	if err != nil {
		return nil, nil, err
	}
	cipherText = aead.Seal(nil, nonce, plain, nil)
	return nonce, cipherText, nil
}

	block, err := aes.NewCipher(key)
	if err != nil {
		return nil, err
	}
	aead, err := cipher.NewGCM(block)
	if err != nil {
		return nil, err
	}
	plain, err := aead.Open(nil, nonce, cipherText, nil)
	if err != nil {
		return nil, err
	}
	return plain, nil
}

	plain, err := os.ReadFile(src)
	if err != nil {
		return err
	}

	salt, err := secureRand(saltSize)
	if err != nil {
		return err
	}

	key, err := deriveKey(passphrase, salt)
	if err != nil {
		return err
	}
	// overwrite key slice later if desired (best-effort)
	nonce, ciphertext, err := encryptBytes(key, plain)
	if err != nil {
		return err
	}

	outF, err := os.OpenFile(dst, os.O_CREATE|os.O_WRONLY|os.O_TRUNC, 0o600) // owner read/write only
	if err != nil {
		return err
	}
	defer outF.Close()

	// write header
	if _, err := outF.Write([]byte(magic)); err != nil {
		return err
	}
	if _, err := outF.Write(salt); err != nil {
		return err
	}
	if _, err := outF.Write(nonce); err != nil {
		return err
	}
	// write ciphertext length (optional but handy) - 8 bytes
	if err := binary.Write(outF, binary.BigEndian, uint64(len(ciphertext))); err != nil {
		return err
	}
	// write ciphertext
	if _, err := outF.Write(ciphertext); err != nil {
		return err
	}

	// best-effort zeroing
	for i := range key {
		key[i] = 0
	}
	return nil
}

	in, err := os.Open(src)
	if err != nil {
		return err
	}
	defer in.Close()

	// read magic
	m := make([]byte, len(magic))
	if _, err := io.ReadFull(in, m); err != nil {
		return err
	}
	if string(m) != magic {
		return fmt.Errorf("invalid file format or magic mismatch")
	}

	salt := make([]byte, saltSize)
	if _, err := io.ReadFull(in, salt); err != nil {
		return err
	}
	nonce := make([]byte, nonceSize)
	if _, err := io.ReadFull(in, nonce); err != nil {
		return err
	}
	// read ciphertext length
	var clen uint64
	if err := binary.Read(in, binary.BigEndian, &clen); err != nil {
		return err
	}
	if clen > (1 << 40) { // sanity cap (~1 TB)
		return errors.New("ciphertext length suspiciously large")
	}
	ciphertext := make([]byte, clen)
	if _, err := io.ReadFull(in, ciphertext); err != nil {
		return err
	}

	key, err := deriveKey(passphrase, salt)
	if err != nil {
		return err
	}
	plain, err := decryptBytes(key, nonce, ciphertext)
	if err != nil {
		return err
	}

	// write decrypted bytes
	if err := os.WriteFile(dst, plain, 0o600); err != nil {
		return err
	}

	// best-effort zero key
	for i := range key {
		key[i] = 0
	}
	return nil
}

	// try to load .env (optional). If you prefer not to use godotenv, set KEY_PASSPHRASE in environment by other means.
	_ = godotenv.Load()

	modeEnc := flag.Bool("encrypt", false, "encrypt mode")
	modeDec := flag.Bool("decrypt", false, "decrypt mode")
	inPath := flag.String("in", "", "input file path")
	outPath := flag.String("out", "", "output file path")
	keyEnv := flag.String("keyenv", "KEY_PASSPHRASE", "environment variable name that contains the passphrase")
	flag.Parse()

	if (*modeEnc && *modeDec) || (!*modeEnc && !*modeDec) {
		fmt.Println("Specify exactly one of -encrypt or -decrypt")
		flag.Usage()
		os.Exit(2)
	}
	if *inPath == "" || *outPath == "" {
		fmt.Println("Both -in and -out must be provided")
		flag.Usage()
		os.Exit(2)
	}

	passphrase := os.Getenv(*keyEnv)
	if passphrase == "" {
		fmt.Printf("passphrase not found in env variable %s\n", *keyEnv)
		os.Exit(2)
	}

	var err error
	if *modeEnc {
		err = encryptFile(*inPath, *outPath, passphrase)
	} else {
		err = decryptFile(*inPath, *outPath, passphrase)
	}
	if err != nil {
		fmt.Fprintf(os.Stderr, "error: %v\n", err)
		os.Exit(1)
	}
	fmt.Println("done")
}

