	m := make([]byte, len(magic))
	if _, err := io.ReadFull(in, m); err != nil {
		return err
	}
	if string(m) != magic {
		return fmt.Errorf("invalid file format or magic mismatch")
	}

	salt := make([]byte, saltSize)
	if _, err := io.ReadFull(in, salt); err != nil {
		return err
	}
	nonce := make([]byte, nonceSize)
	if _, err := io.ReadFull(in, nonce); err != nil {
		return err
	}
	// read ciphertext length
	var clen uint64
	if err := binary.Read(in, binary.BigEndian, &clen); err != nil {
		return err
	}
	if clen > (1 << 40) { // sanity cap (~1 TB)
		return errors.New("ciphertext length suspiciously large")
	}
	ciphertext := make([]byte, clen)
