	if _, err := io.ReadFull(in, salt); err != nil {
		return err
	}
	nonce := make([]byte, nonceSize)
	if _, err := io.ReadFull(in, nonce); err != nil {
		return err
	}
	// read ciphertext length
	var clen uint64
	if err := binary.Read(in, binary.BigEndian, &clen); err != nil {
		return err
	}
	if clen > (1 << 40) { // sanity cap (~1 TB)
