	// write header
	if _, err := outF.Write([]byte(magic)); err != nil {
		return err
	}
	if _, err := outF.Write(salt); err != nil {
		return err
	}
	if _, err := outF.Write(nonce); err != nil {
		return err
	}
	// write ciphertext length (optional but handy) - 8 bytes
	if err := binary.Write(outF, binary.BigEndian, uint64(len(ciphertext))); err != nil {
		return err
	}
	// write ciphertext
	if _, err := outF.Write(ciphertext); err != nil {
