	// try to load .env (optional). If you prefer not to use godotenv, set KEY_PASSPHRASE in environment by other means.
	_ = godotenv.Load()

	modeEnc := flag.Bool("encrypt", false, "encrypt mode")
	modeDec := flag.Bool("decrypt", false, "decrypt mode")
	inPath := flag.String("in", "", "input file path")
	outPath := flag.String("out", "", "output file path")
	keyEnv := flag.String("keyenv", "KEY_PASSPHRASE", "environment variable name that contains the passphrase")
	flag.Parse()

	if (*modeEnc && *modeDec) || (!*modeEnc && !*modeDec) {
		fmt.Println("Specify exactly one of -encrypt or -decrypt")
		flag.Usage()
		os.Exit(2)
	}
	if *inPath == "" || *outPath == "" {
		fmt.Println("Both -in and -out must be provided")
		flag.Usage()
		os.Exit(2)
	}

	passphrase := os.Getenv(*keyEnv)
	if passphrase == "" {
		fmt.Printf("passphrase not found in env variable %s\n", *keyEnv)
		os.Exit(2)
	}

	var err error
	if *modeEnc {
		err = encryptFile(*inPath, *outPath, passphrase)
	} else {
		err = decryptFile(*inPath, *outPath, passphrase)
	}
	if err != nil {
		fmt.Fprintf(os.Stderr, "error: %v\n", err)
		os.Exit(1)
	}
	fmt.Println("done")
